mapper.delete:
update tb_dtms_exam_user_plan_model
set is_deleted = 1
where exam_plan_id =#{examPlanId}

mapper.update:
update tb_dtms_exam_user_plan_model
modify_date = NOW(), is_start = 1,
where exam_plan_id = #{examPlanId,jdbcType=BIGINT}

mapper.select:
select userPlan.biz_id, userPlan.is_finish, userPlan.exam_time, userPlan.exam_plan_id, userPlan.exam_id, userPlan.is_start, userPlan.start_date, userPlan.end_date, userPlan.exam_duration, userPlan.total_points, userPlan.pass_points, userPlan.user_id, userPlan.base_total_points, userPlan.is_mark, plan.exam_plan_name ,plan.create_user_name from tb_dtms_exam_user_plan_model userPlan left join tb_dtms_exam_plan_model plan on userPlan.exam_plan_id = plan.biz_id where plan.is_deleted = 0 and userPlan.user_id = #{userId,jdbcType=BIGINT}
and plan.exam_plan_name like concat(concat('%',#{examPlanName}),'%')
ORDER BY userPlan.create_date DESC
