mapper.select:
select

from

where is_deleted = 0

    and file_no = #{fileNo,jdbcType=VARCHAR}


    and file_name = #{fileName,jdbcType=VARCHAR}


    and biz_id = #{bizId,jdbcType=BIGINT}

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName, file.file_version AS fileVersion,dept.dept_name AS applyDeptName, file.creator AS applyUserName,file.apply_date AS applyDate, file.file_status AS fileStatus,file.file_apply_type AS fileApplyType from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id where file.is_deleted = 0 and dept.is_deleted = 0
and file.file_no = #{fileNo,jdbcType=VARCHAR}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
and file.apply_type = #{applyType}
and apply_user_id = #{applyUserId} order by file.gmt_created desc

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName, file.file_content AS fileContent,file.file_version AS fileVersion,file.effect_date AS effectDate, details.item_name AS fileTypeIdName, dept.dept_name AS applyDeptName, file.creator AS applyUserName,file.approver_user_name AS approverUserName,file.auditor_name AS auditorName from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id left join tb_dtms_qms_item_details details on file.file_type_id = details.biz_id where file.is_deleted = 0 and dept.is_deleted = 0 and details.is_deleted = 0
and file.file_status = #{fileStatus}
and file.file_no = #{fileNo}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
and file.auditor_name LIKE concat(concat('%',#{auditorName,jdbcType=VARCHAR}),'%')
and file.apply_year = #{applyYear,jdbcType=INTEGER}
and file.biz_id in
#{fileId}
order by file.gmt_created desc

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName, details.item_name AS fileTypeIdName, file.file_version AS fileVersion, dept.dept_name AS applyDeptName, file.creator AS applyUserName, file.effect_date AS effectDate, file.file_status AS fileStatus,file.file_apply_type AS fileApplyType from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id left join tb_dtms_qms_item_details details on file.file_type_id = details.biz_id where file.is_deleted = 0 and dept.is_deleted = 0 and details.is_deleted = 0 and file.file_status = 2
and file.file_no = #{fileNo,jdbcType=VARCHAR}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
and file.apply_type = #{applyType,jdbcType=BIGINT}
and POSITION(#{signor1Id} IN signator1_id)
or POSITION(#{signator2Id} IN signator2_id)
or POSITION(#{approverId} IN approver_id)
order by file.gmt_created desc

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName,dept.dept_name AS applyDeptName, file.file_content AS fileContent, file.is_received AS isReceived, file.receive_date AS receiveDate,file.receiver_name AS receiverName from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id where file.is_deleted = 0 and dept.is_deleted = 0
and file.file_no = #{fileNo,jdbcType=VARCHAR}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
and file.biz_id in
#{fileId}
and file.is_received is NULL
order by file.gmt_created desc

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName, dept.dept_name AS applyDeptName,file.file_version AS fileVersion,file.effect_date AS effectDate, file.creator AS applyUserName,file.approver_user_name AS approverUserName,file.auditor_name AS auditorName, file.allow_modify AS allowModify,file.allow_bad AS allowBad,file.apply_status AS applyStatus, file.gmt_modified AS operatorDate,file.modifier AS operatorName from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id where file.is_deleted = 0 and dept.is_deleted = 0
and file.file_no = #{fileNo,jdbcType=VARCHAR}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
and file.apply_type = #{applyType,jdbcType=BIGINT}
order by file.gmt_created desc

select file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName, file.file_version AS fileVersion,dept.dept_name AS applyDeptName, file.creator AS applyUserName,file.apply_date AS applyDate, file.file_status AS fileStatus,file.file_apply_type AS fileApplyType from tb_dtms_file_model file left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id left join tb_dtms_file_year yea on yea.file_id = file.biz_id where file.is_deleted = 0 and dept.is_deleted = 0 and yea.is_deleted = 0
and file.file_no = #{fileNo,jdbcType=VARCHAR}
and file.file_name LIKE concat(concat('%',#{fileName}),'%')
and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}
and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}
and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')
order by file.gmt_created desc

select
file.biz_id AS bizId,file.file_no AS fileNo,file.file_name AS fileName,
file.file_version AS fileVersion,file.effect_date AS effectDate,dept.dept_name AS applyDeptName,
file.gmt_modified AS operatorDate,file.modifier AS operatorName
from
tb_dtms_file_model file
left join tb_dtms_qms_dept dept on file.apply_dept_id = dept.biz_id
where file.is_deleted = 0
and dept.is_deleted = 0

    and file.file_no = #{fileNo,jdbcType=VARCHAR}


    and file.file_name LIKE concat(concat('%',#{fileName}),'%')


    and file.file_type_id = #{fileTypeId,jdbcType=BIGINT}


    and file.apply_dept_id = #{applyDeptId,jdbcType=BIGINT}


    and file.creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')


    and file.apply_type = #{applyType,jdbcType=BIGINT}


    and file.file_status = #{fileStatus,jdbcType=BIGINT}


    and file.biz_id in

        #{fileId}


order by file.gmt_created desc

select
biz_id AS bizId,file_no AS fileNo,file_name AS fileName,
file_version AS fileVersion,
creator AS applyUserName,apply_date AS applyDate,
auditor_name AS auditorName,approver_content AS approverContent
from
tb_dtms_file_model
where is_deleted = 0

    and file_no = #{fileNo,jdbcType=VARCHAR}


    and file_name LIKE concat(concat('%',#{fileName}),'%')


    and file_type_id = #{fileTypeId,jdbcType=BIGINT}


    and apply_dept_id = #{applyDeptId,jdbcType=BIGINT}


    and creator LIKE concat(concat('%',#{applyUser,jdbcType=VARCHAR}),'%')


    and apply_type = #{applyType,jdbcType=BIGINT}


    and file_status = #{fileStatus,jdbcType=BIGINT}

order by gmt_created desc
