mapper.select:
select

from

where is_deleted = 0

    and dept_id = #{dept_id,jdbcType=VARCHAR}


    and train_name = #{trainName,jdbcType=VARCHAR}


    and user_exam_total = #{userExamTotal,jdbcType=VARCHAR}


    and user_id = #{userId,jdbcType=BIGINT}

select user_id AS userId,end_date AS endDate,finish_time AS finishTime from tb_dtms_train_user WHERE begin_time IS NOT NULL and is_deleted = 0
and gmt_created >= #{beginTime,jdbcType=TIMESTAMP} AND gmt_created < #{finishTime,jdbcType=TIMESTAMP}
ORDER BY gmt_created desc

select train.biz_id AS bizId, usr.dsp_name AS dspName, exam.exam_id AS examId, exam.exam_duration AS examDuration, config.total_point AS totalPoint, config.pass_point AS passPoint, train.point_str AS pointStr, train.answer AS answer, questions.questions_type_id AS questionsTypeId, questions.question_title AS questionTitle, questions.question_content AS questionContent, questions.question_a AS questionA, questions.question_b AS questionB, questions.question_c AS questionC, questions.question_d AS questionD, questions.question_e AS questionE, questions.question_f AS questionF from tb_dtms_train_user train LEFT JOIN tb_dtms_qms_user usr ON train.user_id = usr.biz_id LEFT JOIN tb_dtms_exam_user_plan_model exam ON train.user_id = exam.user_id LEFT JOIN tb_dtms_train_config config ON train.train_id = config.biz_id LEFT JOIN tb_dtms_questions questions ON train.type_id = questions.exam_type_id WHERE train.is_finish_questions = 1
and train.train_id = #{trainId}
and train.is_deleted = 0 and usr.is_deleted = 0 and config.is_deleted = 0 and questions.is_deleted = 0 order by any_value(train.gmt_created) desc

mapper.delete:
update

set
is_deleted = 1


        and train_id = #{trainId,jdbcType=BIGINT}


        and user_id = #{userId,jdbcType=BIGINT}

mapper.select:
